import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Search.css';

export default function Search() {
  const [url, setUrl] = useState('');
  const [isValid, setIsValid] = useState(false);
  const [showErrorMessage, setShowErrorMessage] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    // input validation
    if (url.includes('airbnb.com/')) {
      setIsValid(true);
      setShowErrorMessage(false);
    }
  }, [url]);

  const submitUrl = async (e) => {
    e.preventDefault();
    if (isValid) {
      navigate('/results', { state: { url: url } });
    } else {
      //throw an error
      setShowErrorMessage(true);
    }
  };

  return (
    <>
      <div className='search__container d-flex'>
        <h2 className='search__h2'>Paste your AirBnB listing URL here</h2>
        <input
          className='search__input align-self-center'
          type='text'
          value={url}
          onChange={(e) => {
            setUrl(e.target.value);
          }}
          placeholder='Paste listing URL'></input>
        <button
          className='search__button'
          type='submit'
          onClick={(e) => {
            submitUrl(e);
          }}>
          Submit
        </button>
      </div>
      {showErrorMessage && <div>Invalid Input</div>}
    </>
  );
}
