import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';

function App() {
  const [url, setUrl] = useState('');
  const [isValid, setIsValid] = useState(false);
  const [showErrorMessage, setShowErrorMessage] = useState(false);

  const navigate = useNavigate();

  const handleValidation = () => {
    console.log(url);
    if (url.includes('airbnb.com/')) {
      setIsValid(true);
    }
  };

  // TODO
  //understand why setUrl is delayed

  const submitUrl = async (e) => {
    e.preventDefault();
    handleValidation();
    if (isValid) {
      navigate('/test');
    } else {
      //throw an error
      setShowErrorMessage(true);
    }
  };

  const renderSearchBar = () => {
    return (
      <div>
        <input
          type='text'
          value={url}
          onChange={(e) => {
            setUrl(e.target.value);
          }}
          placeholder='Paste listing URL'></input>
        <button
          type='submit'
          onClick={(e) => {
            submitUrl(e);
          }}>
          Submit
        </button>
      </div>
    );
  };

  return (
    <div className='App'>
      {renderSearchBar()}
      {showErrorMessage && <div>Invalid Input</div>}
    </div>
  );
}

export default App;
