import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Search.css';

export default function Search() {
  const [url, setUrl] = useState('');
  const [isValid, setIsValid] = useState(false);
  const [showErrorMessage, setShowErrorMessage] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    // TODO: expand input validation
    if (url.includes('airbnb.com/')) {
      setIsValid(true);
      setShowErrorMessage(false);
    }
  }, [url]);

  const submitOnEnter = (e) => {
    if (e.keyCode === 13) {
      submitUrl(e);
    }
  };

  const submitUrl = async (e) => {
    e.preventDefault();
    if (isValid) {
      navigate('/results', { state: { url: url } });
    } else {
      //throw an error
      setShowErrorMessage(true);
    }
  };

  return (
    <>
      {/* desktop */}
      <div>
        <div className='search__container text-center'>
          <div className='search__content-container'>
            <h2 className='search__h2'>Paste your AirBnB listing URL here</h2>
            <input
              className={`search__input m-4 ${
                showErrorMessage ? 'search__input__invalid' : ''
              }`}
              type='text'
              value={url}
              onChange={(e) => {
                setUrl(e.target.value);
                setShowErrorMessage(false);
              }}
              onKeyDown={(e) => submitOnEnter(e)}></input>
            <button
              className='search__button'
              type='submit'
              onClick={(e) => {
                submitUrl(e);
              }}>
              Submit
            </button>
            {showErrorMessage && (
              <div className='search__invalid-input'>Invalid Input</div>
            )}
          </div>
        </div>
      </div>
      {/* mobile */}
      <div className='search__container d-flex align-items-center justify-content-center'>
        <div>
          <h2 className='search__h2'>Paste your AirBnB listing URL here</h2>
          <input
            className={`search__input mx-2 ${
              showErrorMessage ? 'search__input__invalid' : ''
            }`}
            type='text'
            value={url}
            onChange={(e) => {
              setUrl(e.target.value);
              setShowErrorMessage(false);
            }}
            onKeyDown={(e) => submitOnEnter(e)}></input>
          <button
            className='search__button'
            type='submit'
            onClick={(e) => {
              submitUrl(e);
            }}>
            Submit
          </button>
          {showErrorMessage && (
            <div className='search__invalid-input'>Invalid Input</div>
          )}
        </div>
      </div>
    </>
  );
}
